import React from 'react';
import { View, Text, Button, StyleSheet, StatusBar,ScrollView,Image } from 'react-native';
import { useTheme } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Ionicons';
import { Avatar, Card, Title, Paragraph } from 'react-native-paper';
import * as Animatable from 'react-native-animatable';
import {LinearGradient} from 'expo-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import PoliticalCat from '../components/PoliticalCat';


const HomeScreen = ({navigation}) => {

  const { colors } = useTheme();

  const theme = useTheme();



    return (
<View style={styles.container}>
<Text style={styles.ptitle}>Political Banner </Text>
<ScrollView horizontal>
<PoliticalCat/>
</ScrollView>

        <StatusBar barStyle= { theme.dark ? "light-content" : "dark-content" }/>
        <Text style={{color: colors.text}}>Home Screenddd</Text>
      <Button
        title="Go to details screen"
        onPress={() => navigation.navigate("Details")}
      />
      </View>
    );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    justifyContent: 'center'
  },
  picon:{
width:114,
height:113
},
ptitle:{
  fontSize:20,
  margin:10,
  color:'#333'

  }
});
