import React, {useState} from 'react';
import { View, Text, StyleSheet,ScrollView,SectionList } from 'react-native';
import * as Animatable from 'react-native-animatable';



class politicalCat extends React.Component{
constructor()
{
super();
}


render(){
 

  const dataList = [
    {
      CatFullName:'Bhartiya Janta Party',
      CatShortName:'BJP',
      CatImage:'../assets/political/bjp.png'
    },
    {
      CatFullName:'Congress Party',
      CatShortName:'Congress',
      CatImage:'../assets/political/Congress.png'
    },
    {
      CatFullName:'AAP Party',
      CatShortName:'AAP',
      CatImage:'../assets/political/AAP.png'
    }
    ];
    

    var politicalView = [];

    for(let i = 0; i < dataList; i++){
  
      politicalView.push(
      <View style={styles.header}>
        <Animatable.Image  
                       animation="bounceIn"
                       duraton="1500"
                   source={require('../assets/political/AAP.png')}
                   style={styles.picon}
                   resizeMode="stretch"
                   />
                   <Text style={styles.ptext}>BJP</Text>
                 </View> 
      )
    }
 




  
    return (



      
     
<ScrollView horizontal={true}>

{politicalView}
         

          


</ScrollView>


 );
};
};
  

export default politicalCat;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center'
  },
  picon : {
width:100,
height:100
  },
  ptext:{
    alignContent:'center',
    textAlign:'center',
    fontSize:17
  },
  header:{
    flex:2

  }

});
