import React, {useState} from 'react';
import { View, Text, StyleSheet,ScrollView } from 'react-native';
import * as Animatable from 'react-native-animatable';



  class politicalCat extends React.Component{
constructor()
{
super();
this.state = {
data:[
{
  CatFullName:'Bhartiya Janta Party',
  CatShortName:'BJP',
  CatImage:'../assets/political/bjp.png'
},
{
  CatFullName:'Congress Party',
  CatShortName:'Congress',
  CatImage:'../assets/political/Congress.png'
},
{
  CatFullName:'AAP Party',
  CatShortName:'AAP',
  CatImage:'../assets/political/AAP.png'
}

]

}

}


render(){
    return (
   
<ScrollView horizontal={true}>
<View style={styles.header}>

        <Animatable.Image 
                animation="bounceIn"
                duraton="1500"
            source={require('../assets/political/bjp.png')}
            style={styles.picon}
            resizeMode="stretch"
            />
            <Text style={styles.ptext}>BJP</Text>
        </View>
</ScrollView>

 );
};
  };
  

export default politicalCat;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center'
  },
  picon : {
width:100,
height:100
  },
  ptext:{
    alignContent:'center',
    textAlign:'center',
    fontSize:17
  }
});
