import React from 'react';
import { View, Text, Button, StyleSheet, StatusBar,ScrollView,Image } from 'react-native';
import { useTheme } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Ionicons';
import { Avatar, Card, Title, Paragraph } from 'react-native-paper';


const HomeScreen = ({navigation}) => {

  const { colors } = useTheme();

  const theme = useTheme();
  const LeftContent = props => <Avatar.Icon {...props} icon="folder" />

    return (
      <View style={styles.container}>

<ScrollView horizontal={true}>



<Card>
    <Card.Title title="Political Parties Posters" subtitle="Card Subtitle" left={LeftContent} />
    <Card.Content>
      <Title>BJP</Title>
    </Card.Content>
    <Card.Cover source={require('assets/political/bjp.png')} />

  </Card>


</ScrollView>
        <StatusBar barStyle= { theme.dark ? "light-content" : "dark-content" }/>
        <Text style={{color: colors.text}}>Home Screen</Text>
      <Button
        title="Go to details screen"
        onPress={() => navigation.navigate("Details")}
      />
      </View>
    );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    alignItems: 'center', 
    justifyContent: 'center'
  },
});
